mapper.select:
select data_center AS "dataCenter", app_name AS "appName", ip AS "ip", host_name AS "hostName", port AS "port", process_no AS "processNo", quota_name AS "quotaName", data_value AS "dataValue", data_desc AS "dataDesc", collect_time AS "collectTime" from hbq_quota_data
and data_center=#{dataCenter} and ip=#{ip} and port=#{port} and collect_time >= #{startTime} and collect_time < #{endTime}
order by collectTime asc
